            if (copyMask?.Name ?? true)
            {
                errorMask?.PushIndex((int)Artist_FieldIndex.Name);
                item.Name = rhs.Name;
                errorMask?.PopIndex();
            }
            if (copyMask?.Albums.Overall != CopyOption.Skip)
            {
                errorMask?.PushIndex((int)Artist_FieldIndex.Albums);
                try
                {
                    item.Albums.SetToWithDefault(
                        rhs.Albums,
                        def?.Albums,
                        cmds,
                        (r, d) =>
                        {
                            switch (copyMask?.Albums.Overall ?? CopyOption.Reference)
                            {
                                case CopyOption.Reference:
                                    return r;
                                case CopyOption.MakeCopy:
                                    return Album.Copy(
                                        r,
                                        copyMask?.Albums?.Specific,
                                        def: d);
                                default:
                                    throw new NotImplementedException($"Unknown CopyOption {copyMask?.Albums.Overall}. Cannot execute copy.");
                            }
                        }
                        );
                }
                catch (Exception ex)
                when (errorMask != null)
                {
                    errorMask.ReportException(ex);
                }
                finally
                {
                    errorMask?.PopIndex();
                }
            }
        }

