        public static Artist_Mask<bool> GetEqualsMask(
            this IArtistGetter item,
            IArtistGetter rhs)
        {
            var ret = new Artist_Mask<bool>();
            FillEqualsMask(item, rhs, ret);
            return ret;
        }

        public static void FillEqualsMask(
            IArtistGetter item,
            IArtistGetter rhs,
            Artist_Mask<bool> ret)
        {
            if (rhs == null) return;
            ret.Name = object.Equals(item.Name, rhs.Name);
            ret.Albums = new MaskItem<bool, IEnumerable<MaskItem<bool, Album_Mask<bool>>>>();
            ret.Albums.Specific = item.Albums.Values.SelectAgainst<Album, MaskItem<bool, Album_Mask<bool>>>(rhs.Albums.Values, ((l, r) =>
            {
                MaskItem<bool, Album_Mask<bool>> itemRet;
                itemRet = new MaskItem<bool, Album_Mask<bool>>();
                itemRet.Specific = AlbumCommon.GetEqualsMask(l, r);
                itemRet.Overall = itemRet.Specific.AllEqual((b) => b);
                return itemRet;
            }
            ), out ret.Albums.Overall);
            ret.Albums.Overall = ret.Albums.Overall && ret.Albums.Specific.All((b) => b.Overall);
        }

