        #region Xml Translation
        #region Xml Write
        public static void Write_Xml(
            XElement node,
            Artist item,
            bool doMasks,
            out Artist_ErrorMask errorMask,
            Artist_TranslationMask translationMask,
            string name = null)
        {
            ErrorMaskBuilder errorMaskBuilder = doMasks ? new ErrorMaskBuilder() : null;
            Write_Xml(
                node: node,
                name: name,
                item: item,
                errorMask: errorMaskBuilder,
                translationMask: translationMask?.GetCrystal());
            errorMask = Artist_ErrorMask.Factory(errorMaskBuilder);
        }

        public static void Write_Xml(
            XElement node,
            Artist item,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask,
            string name = null)
        {
            var elem = new XElement(name ?? "MusicRecord.Artist");
            node.Add(elem);
            if (name != null)
            {
                elem.SetAttributeValue("type", "MusicRecord.Artist");
            }
            if ((translationMask?.GetShouldTranslate((int)Artist_FieldIndex.Name) ?? true))
            {
                StringXmlTranslation.Instance.Write(
                    node: elem,
                    name: nameof(item.Name),
                    item: item.Name,
                    fieldIndex: (int)Artist_FieldIndex.Name,
                    errorMask: errorMask);
            }
            if ((translationMask?.GetShouldTranslate((int)Artist_FieldIndex.Albums) ?? true))
            {
                try
                {
                    errorMask?.PushIndex((int)Artist_FieldIndex.Albums);
                    KeyedDictXmlTranslation<String, Album>.Instance.Write(
                        node: elem,
                        name: nameof(item.Albums),
                        items: item.Albums.Values,
                        translationMask: translationMask,
                        errorMask: errorMask,
                        valTransl: (XElement subNode, Album subItem, ErrorMaskBuilder dictSubMask, TranslationCrystal dictTranslMask) =>
                        {
                            LoquiXmlTranslation<Album>.Instance.Write(
                                node: subNode,
                                item: subItem,
                                name: null,
                                errorMask: dictSubMask,
                                translationMask: dictTranslMask);
                        }
                        );
                }
                catch (Exception ex)
                when (errorMask != null)
                {
                    errorMask.ReportException(ex);
                }
                finally
                {
                    errorMask?.PopIndex();
                }
            }
        }
        #endregion

        #endregion

