    public class Artist_Mask<T> : IMask<T>, IEquatable<Artist_Mask<T>>
    {
        #region Ctors
        public Artist_Mask()
        {
        }

        public Artist_Mask(T initialValue)
        {
            this.Name = initialValue;
            this.Albums = new MaskItem<T, IEnumerable<MaskItem<T, Album_Mask<T>>>>(initialValue, null);
        }
        #endregion

        #region Members
        public T Name;
        public MaskItem<T, IEnumerable<MaskItem<T, Album_Mask<T>>>> Albums;
        #endregion

        #region Equals
        public override bool Equals(object obj)
        {
            if (!(obj is Artist_Mask<T> rhs)) return false;
            return Equals(rhs);
        }

        public bool Equals(Artist_Mask<T> rhs)
        {
            if (rhs == null) return false;
            if (!object.Equals(this.Name, rhs.Name)) return false;
            if (!object.Equals(this.Albums, rhs.Albums)) return false;
            return true;
        }
        public override int GetHashCode()
        {
            int ret = 0;
            ret = ret.CombineHashCode(this.Name?.GetHashCode());
            ret = ret.CombineHashCode(this.Albums?.GetHashCode());
            return ret;
        }

        #endregion

        #region All Equal
        public bool AllEqual(Func<T, bool> eval)
        {
            if (!eval(this.Name)) return false;
            if (this.Albums != null)
            {
                if (!eval(this.Albums.Overall)) return false;
                if (this.Albums.Specific != null)
                {
                    foreach (var item in this.Albums.Specific)
                    {
                        if (!eval(item.Overall)) return false;
                        if (!item.Specific?.AllEqual(eval) ?? false) return false;
                    }
                }
            }
            return true;
        }
        #endregion

        #region Translate
        public Artist_Mask<R> Translate<R>(Func<T, R> eval)
        {
            var ret = new Artist_Mask<R>();
            this.Translate_InternalFill(ret, eval);
            return ret;
        }

        protected void Translate_InternalFill<R>(Artist_Mask<R> obj, Func<T, R> eval)
        {
            obj.Name = eval(this.Name);
            if (Albums != null)
            {
                obj.Albums = new MaskItem<R, IEnumerable<MaskItem<R, Album_Mask<R>>>>();
                obj.Albums.Overall = eval(this.Albums.Overall);
                if (Albums.Specific != null)
                {
                    List<MaskItem<R, Album_Mask<R>>> l = new List<MaskItem<R, Album_Mask<R>>>();
                    obj.Albums.Specific = l;
                    foreach (var item in Albums.Specific)
                    {
                        MaskItem<R, Album_Mask<R>> mask = default(MaskItem<R, Album_Mask<R>>);
                        if (item != null)
                        {
                            mask = new MaskItem<R, Album_Mask<R>>();
                            mask.Overall = eval(item.Overall);
                            if (item.Specific != null)
                            {
                                mask.Specific = item.Specific.Translate(eval);
                            }
                        }
                        l.Add(mask);
                    }
                }
            }
        }
        #endregion

        #region Clear Enumerables
        public void ClearEnumerables()
        {
            this.Albums.Specific = null;
        }
        #endregion

        #region To String
        public override string ToString()
        {
            return ToString(printMask: null);
        }

        public string ToString(Artist_Mask<bool> printMask = null)
        {
            var fg = new FileGeneration();
            ToString(fg, printMask);
            return fg.ToString();
        }

        public void ToString(FileGeneration fg, Artist_Mask<bool> printMask = null)
        {
            fg.AppendLine($"{nameof(Artist_Mask<T>)} =>");
            fg.AppendLine("[");
            using (new DepthWrapper(fg))
            {
                if (printMask?.Name ?? true)
                {
                    fg.AppendLine($"Name => {Name}");
                }
                if (printMask?.Albums?.Overall ?? true)
                {
                    fg.AppendLine("Albums =>");
                    fg.AppendLine("[");
                    using (new DepthWrapper(fg))
                    {
                        if (Albums.Overall != null)
                        {
                            fg.AppendLine(Albums.Overall.ToString());
                        }
                        if (Albums.Specific != null)
                        {
                            foreach (var subItem in Albums.Specific)
                            {
                                fg.AppendLine("[");
                                using (new DepthWrapper(fg))
                                {
                                    fg.AppendLine($"Name => {subItem}");
                                }
                                fg.AppendLine("]");
                            }
                        }
                    }
                    fg.AppendLine("]");
                }
            }
            fg.AppendLine("]");
        }
        #endregion

    }

