        #region Combine
        public Artist_ErrorMask Combine(Artist_ErrorMask rhs)
        {
            var ret = new Artist_ErrorMask();
            ret.Name = this.Name.Combine(rhs.Name);
            ret.Albums = new MaskItem<Exception, IEnumerable<MaskItem<Exception, Album_ErrorMask>>>(this.Albums.Overall.Combine(rhs.Albums.Overall), new List<MaskItem<Exception, Album_ErrorMask>>(this.Albums.Specific.And(rhs.Albums.Specific)));
            return ret;
        }
        public static Artist_ErrorMask Combine(Artist_ErrorMask lhs, Artist_ErrorMask rhs)
        {
            if (lhs != null && rhs != null) return lhs.Combine(rhs);
            return lhs ?? rhs;
        }
